import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
  state ={
    title:"",
    url: "",
    image:[]
  }

  addURL =(e) =>{

   const url = this.state.url;
    this.setState({

      url: e.target.value,
    })
  }

  addImage = () => {
    const image = this.state.image;
    image.push(this.state.url);
    console.log(image);



  }




  render() {
    return (
      <div className="App">
         URL: <input onChange={this.addURL} value={this.state.url}></input><br/>
         <span>TITLE:<input></input> <button onClick={this.addImage}>POST</button></span>
      </div>
    );
  }
}

export default App;
