import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
  state ={
    title:"",
    url: "",
    image:[]
  }

  addTitle = (e) => {  
    this.setState({
      title:e.target.value
    })
  }

  addURL =(e) =>{

   let url = this.state.url;
    this.setState({

      url: e.target.value,
    })
  }



  addImage = () => {
    let arr1 = this.state.image;
    arr1.push({url:this.state.url, title:this.state.title})
    this.setState({
      image:arr1,
      url:"",
      title: "",
    })
  }



  render() {
    return (
      <div className="App">
      <div>
         URL: <input onChange={this.addURL} value={this.state.url}></input><br/>
         <lable>TITLE:</lable><br/>
         <span><input className="titleField" onChange={this.addTitle} ></input> <button className="button" onClick={this.addImage}>POST</button></span>

      </div>

        {
          this.state.image.map((ele)=>{
              return <div>
                <h1>{ele.title}</h1>
                <img src ={ele.url} alt="image1" key ={Math.random()}/>
              </div>

          })
        }



      </div>
    )

    }  
}

export default App;
