import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';


class App extends Component {
  state ={
    title:"",
    url: "",
    image:[]
  }

  addURL =(e) =>{

   let url = this.state.url;
    this.setState({

      url: e.target.value,
    })
  }

  addImage = () => {
    let image = this.state.image;
    image.push(this.state.url);
    console.log(image);
    this.setState({
      url:"",
      title: "",
      image

    })
  }

  addTitle = (e) => {
    let title = this.state.title;
    this.setState({
      title : e.target.value
    })
  }

  render() {
    return (
      <div className="App">
      <div>
         URL: <input onChange={this.addURL} value={this.state.url}></input><br/>
         <span>TITLE:<input onChange={this.addTitle} value={this.state.title}></input> <button onClick={this.addImage}>POST</button></span>
      </div>

        {
          this.state.image.map((ele,id)=>{
              return <div>
                <h1>{this.state.title}</h1>
                <img src ={ele} alt="image1" key ={id}/>
              </div>

          })
        }



      </div>
    )

    }  
}

export default App;
